            headers:{
                "content-Type":"application/json",
            }
        };

        try{
            await axios.put(`http://localhost:8070/movie/movies/update/${props.match.params.id}`,updatedMovie,config);
            alert("Updated Successfully")
            history.push('/movie/movies')
        } catch (error){
                alert("Updating failed")
            }
        }


