import React, {useState} from 'react';
import { Link, useHistory } from 'react-router-dom';
import IconButton from "@material-ui/core/IconButton";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import axios from 'axios';
import '../CustomerManagement/SignIn/SignIn.css';
import "./AdminLogin.css";

function AdminLogin() {

    const [showPassword, setShowPassword] = useState()
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory();

    //show hide password
    function handleShowPassword(){
        setShowPassword((prevShowPassword) => !prevShowPassword)
    }

    async function signIn(event){
        event.preventDefault();

        const config = {
            headers: {
                "content-Type": "application/json"
            }
        };

        try {
            //getting data from backend
            const {data} = await axios.post("http://localhost:8070/admin/signin", {email, password}, config);

            //setting the customer authorization token
            localStorage.setItem("adminAuthToken", `Admin ${data.token}`)
            //setting user
            localStorage.setItem("user", JSON.stringify(data.result))

            history.push('/')
        } catch (error) {
            if(error.response.status === 404){
                alert("Invalid Email")
            }
            else if(error.response.status === 400){
                alert("Password Incorrect")
            }
            else{
                alert("Authentication Failed")
            }
        }
    }

    return (
        <div align="center" className="bg_signin">

<img
        className="homeimg"
        src="../images/downloadApp9.jpg"
        alt="download from store"
      />

        <div className="" align="center"><br /> <br /> <br />
            <div className="card-form">
                <form className="boxSignIn" onSubmit={signIn}>
                    <h1 className="form-h1">Admin Login</h1>
                    <p className="text-muted"> All your Movie needs at one place!</p> 
                    <input 
                        type="email" 
                        name="email" 
                        id="email"
                        placeholder="E-mail" 
                        onChange={(event)=> {setEmail(event.target.value)}} 
                        required 
                    />

                    <input
                        type={showPassword ? "text" : "password"} 
                        name="password"
                        id="password" 
                        placeholder="Password" 
                        onChange={(event)=> {setPassword(event.target.value)}} 
                        handleShowPassword={handleShowPassword}  
                        required 
                    />
                    <span className="showhide">
                        <IconButton onClick={handleShowPassword} >
                            {showPassword ? <Visibility /> : <VisibilityOff />}
                        </IconButton>
                    </span>

                    <input className="form-submit-btn" type="submit" value="Sign In" />

                    <br></br><br></br>
                    <div className="text-muted">
                        <p>Are you a Customer? <Link to="/customer/signin">Click here</Link></p>
                        <p>Are you a doctor? <Link to="/doctor/signin"> Click here</Link></p>
                    </div>
                </form>
            </div>
        </div></div>
    )
}

export default AdminLogin
